Intake.header:
void start();
  void stop();
  void reverse();

source.OneController:
void [CLASSNAME]::start() {
  if (!kInverted)
  {
    mIntakeMotor.Set(kSpeed);
  }

 else
  {
     mIntakeMotor.Set(-kSpeed);
  }
}

void [CLASSNAME]::stop() {
  mIntakeMotor.Set(0);
}

void [CLASSNAME]::reverse() {
  kInverted = !kInverted;
}

source.TwoControllers:
void [CLASSNAME]::start() {
  if (!kInverted)
  {
    mIntakeLeft.Set(kSpeed);
mIntakeRight.Set(-kSpeed);
  }

 else
  {
     mIntakeLeft.Set(-kSpeed);
mIntakeRight.Set(kSpeed);
  }
}

void [CLASSNAME]::stop() {
  mIntakeLeft.Set(0);
  mIntakeRight.Set(0);
}

void [CLASSNAME]::reverse() {
  kInverted = !kInverted;
}
